#!/usr/bin/env python3

import argparse
import sys

class opt:
    def check(self):
        p = argparse.ArgumentParser(prog='spinfo')
        eps = p.add_mutually_exclusive_group()
        etu = p.add_mutually_exclusive_group()
        eps.add_argument('-S','--service',nargs='+')
        eps.add_argument('-P','--port',nargs='+')
        etu.add_argument('-T','--tcp',action='store_true')
        etu.add_argument('-U','--udp',action='store_true')
        return p.parse_args()
