#!/usr/bin/env python3

class tcp:
    def tcpFilter(self,info):
        findStr = "/" + "tcp"
        return [
            line for line in info if line.find(findStr) > 0
        ]

class udp:
    def udpFilter(self,info):
        findStr = "/" + "udp"
        return [
            line for line in info if line.find(findStr) > 0
        ]
