#!/usr/bin/env python3

class msg:
    def fields(self,fieldType):
        port = "PORT NUMBER"
        srvc = "SERVICE NAME"
        proto = "PROTOCOL"
        desc = "SERVICE DESCRIPTION"
        if fieldType == "srvc":
            print(
                "\n{srvc:^15} "
                "{port:^15} "
                "{proto:^10} "
                "{desc:^40}".format(**locals())
            )
        elif fieldType == "port":
            print(
                "\n{port:^15} "
                "{srvc:^15} "
                "{proto:^10} "
                "{desc:^40}".format(**locals())
            )
        print("-" * 15,"-" * 15,"-" * 10,"-" * 40)
    def info(self,fieldType,fieldData):
        for line in fieldData:
            fieldVals = list()
            srvcPort = dict()
            if line:
                fieldVals = line.split()
                fieldVals[1:2] = fieldVals[1].split('/')
                fieldVals[3:] = [" ".join(fieldVals[3:]).lstrip('# '),]
                srvcPort = dict(
                    zip(
                        ("srvc","port","proto","desc"),
                        (
                            fieldVals[0]
                            ,fieldVals[1]
                            ,fieldVals[2]
                            ,fieldVals[3]
                        )
                    )
                )
            if fieldType == "srvc":
                print(
                    "{srvc:^15} "
                    "{port:>15} "
                    "{proto:^10} "
                    "{desc:<40}".format(**srvcPort)
                )
            elif fieldType == "port":
                print(
                    "{port:>15} "
                    "{srvc:^15} "
                    "{proto:^10} "
                    "{desc:<40}".format(**srvcPort)
                )
    def noInfo(
        self
        ,fieldType
        ,srvc="-service NA-"
        ,port="-port NA-"
        ,proto="-proto NA-"
        ,desc="-Info Not Available (NA)-"
        ):
        if fieldType == "srvc":
            print(
                "{srvc:^15} "
                "{port:^15} "
                "{proto:^10} "
                "{desc:^40}".format(**locals())
            )
        elif fieldType == "port":
            print(
                "{port:^15} "
                "{srvc:^15} "
                "{proto:^10} "
                "{desc:^40}".format(**locals())
            )
